package com.nidhi.study;

import java.util.Scanner;

public class tables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 2; i <= 5; i++)
			{for(int f=1; f<11; f++)
			 { System.out.println(i+" * "+f+" = "+ i*f);
			  
			 }
			System.out.println("  ");
			}
		for (int a = 1; a <=10 ; a++) {
			for(int b=2; b<6; b++)
			{
				System.out.print( a*b +"  ");
			}
			System.out.println(" ");
		}
	}

 }
